@app.post('/run')
async def run_flow(data: InputData):
    try:
        input_value = data.inputs.get('input_value', '') if data.inputs else ''
        graph = main(input_value=input_value)
        
        # Handle Session ID for memory
        if data.session_id:
            graph.session_id = data.session_id
            for vertex_id in graph.has_session_id_vertices:
                vertex = graph.get_vertex(vertex_id)
                if vertex:
                    vertex.update_raw_params({'session_id': data.session_id})
        
        results = []

        # Run the graph and collect results
        async for result in graph.async_start():
            if hasattr(result, 'vertex'):
                 # Serialize result
                 try:
                     res_val = result.result_dict
                     if hasattr(res_val, 'model_dump'):
                         res_val = res_val.model_dump()
                     elif hasattr(res_val, 'dict'):
                         res_val = res_val.dict()
                     else:
                         res_val = str(res_val)
                 except Exception:
                     res_val = str(result.result_dict)

                 results.append({
                     'vertex_id': result.vertex.id,
                     'result': res_val
                 })

        return {'results': results}
    except Exception as e:
        import traceback
        traceback.print_exc()
        raise HTTPException(status_code=500, detail=str(e))

